@FMAL_FAZLASI FLOAT,@FMIADI DATETIME,@FAKOD VARCHAR(2),@FTANIM VARCHAR(2),@FGRUP VARCHAR(3),@FNAME1 VARCHAR(35),@FADI1 VARCHAR(50)

