 @FTOPLAM_IND=SUM(ROUND((MIKTAR*ETIKET_FIYATI*OZEL_INDIRIM/100),2))
 FROM NX_HAREKET_KALEMLERI WHERE HAREKET_ID=@HAREKET_ID

