        	SELECT MALZEME, MIKTAR, ETIKET_FIYATI ,ECZACI_KARI,MAL_FAZLASI,MIADI,OZEL_INDIRIM,KDV
        	FROM NX_HAREKET_KALEMLERI 
       	WHERE HAREKET_ID = @HAREKET_ID 
       	ORDER BY MALZEME 

