	INTO @FMALZEME, @FMIKTAR, @FETIKET_FIYATI,@FECZACI_KARI,@FMAL_FAZLASI,@FMIADI,@FOZEL_INDIRIM,@FKDV

	WHILE @@FETCH_STATUS = 0 
