	END

		FETCH NEXT FROM kalemler_cursor 
   		INTO @FMALZEME, @FMIKTAR, @FETIKET_FIYATI,@FOZEL_INDIRIM,@FKDV
	END 

	CLOSE kalemler_cursor 
