	UPDATE NX_HAREKETLER SET DURUM='A' /*AKTARILDI*/ WHERE HAREKET_ID=@HAREKET_ID
	IF @@ROWCOUNT=0 goto RLLBCK

	COMMIT TRAN
	RETURN 1 /* 1=BAŞARILI, 0=BAŞARISIZ */

