AS
SELECT  BEHAND.KNR, BEHAND.SNR,HIZILGONSAGNET.GUID,0 AS SIRANO,BEHAND.BHDAT AS GELISTAR,ADRES.HASTAADI, ADRES.HASTASOYADI,BEHAND.SERVIS, BEHAND.AMBU, BEHAND.HASTALIKSONU, 
