left join TET_ANAMNEZ  TA ON TA.KNR = BD.KNR AND    TA.SNR = BD.SNR AND BD.GELISTARIHI=TA.GELIS_TARIHI
WHERE BD.KNR = @KNR AND BD.SNR = @SNR AND BD.AMBU='Y'
 


