-- D : Diğer
-- H : Havuzdakiler
-- T : Tümü
-- HAVUZ_DURUMU
-- H : Hizmet
-- F : Fatura

 SELECT B.TAKIPNO,
        B.HASTABASNO,
        B.KNR AS HastaNo,
        B.SNR,
        B.BHDAT,  
        (CASE WHEN REPLACE(REPLACE(AMBU,'.',''),' ','') = 'P'  THEN B.BHDAT ELSE CONVERT(VARCHAR, B.CIKTAR, 104) END), 
        A.HASTAADI                 AS HastaAdı,
