   FROM BEHAND    B LEFT OUTER JOIN 
        DOKTOR    D ON D.SLB = B.ARZT
                    LEFT OUTER JOIN 
        SERVISLER S ON S.SLB = D.SLXX
                    INNER JOIN 
        ADRES     A ON A.KNR = B.KNR 
                    INNER JOIN 
        KURADR    K ON K.PSG = A.PSG
  WHERE TAKIPNO IS NOT NULL 
