    AND K.KURUMTIPI  LIKE '%'+ @KTIP
    AND B.TEDAVITURU LIKE '%'+ @AMBU
    AND CONVERT(VARCHAR(4),S.SLXA) LIKE '%'+ @BRANS
    AND B.ARZT LIKE '%'+ @DR
  ORDER 
     BY B.HASTABASNO
